/*==================================================
src/components/Home.js

The Home component is used to demonstrate the use of Link.
==================================================*/
import React, { Component } from 'react';
import AccountBalance from './AccountBalance';
import { Link } from 'react-router-dom';
import styled, { keyframes } from 'styled-components';

// Keyframes for animation
const fadeIn = keyframes`
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
`;

// Styled components
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  background-image: url('https://t3.ftcdn.net/jpg/05/77/15/42/360_F_577154228_0IYhXHBvV0MoaSIgKHNKvg3gXrFPy6k8.jpg'); 
  background-size: cover;
  background-position: center;
  min-height: 100vh;
`;

const Logo = styled.img`
  width: 200px;
  height: 200px;
  border-radius: 50%;
  margin-bottom: 20px;
  animation: ${fadeIn} 1s ease-in-out;
`;

const Title = styled.h1`
  font-size: 36px;
  margin-bottom: 20px;
  color: #333;
  animation: ${fadeIn} 1s ease-in-out;
`;

const StyledLink = styled(Link)`
  font-size: 18px;
  color: #007bff;
  text-decoration: none;
  margin-bottom: 10px;
  animation: ${fadeIn} 1s ease-in-out;

  &:hover {
    text-decoration: underline;
  }
`;

class Home extends Component {
  render() {
    return (
      <Container>
        <Logo src="https://picsum.photos/200/200" alt="bank" />
        <Title>Bank of React</Title>
        <StyledLink to="/userProfile">User Profile</StyledLink>
        <StyledLink to="/login">Login</StyledLink>
        <StyledLink to="/credits">Credits (to be implemented in the Assignment)</StyledLink>
        <StyledLink to="/debits">Debits (to be implemented in the Assignment)</StyledLink>
        <AccountBalance accountBalance={this.props.accountBalance} />
      </Container>
    );
  }
}

export default Home;
