import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import styled, { keyframes } from 'styled-components';

// Keyframes for animation
const fadeIn = keyframes`
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
`;

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  background-image: url('https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/325143b6-4839-4676-b7f8-6892f9ba66c4/dfn3efm-748d1037-4fef-4ba0-945d-a5132355bf1c.jpg/v1/fill/w_1280,h_854,q_75,strp/unusual_planets_season_2___8k_wallpapers_series__6_by_dolbozhuy_dfn3efm-fullview.jpg?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..CW50f3WxYmkK-uiM4HudxEpicIJ1r9I21NeR8HLtra8'); 
  background-size: cover;
  background-position: center;
  min-height: 100vh;
`;

const Title = styled.h1`
  font-size: 36px;
  margin-bottom: 20px;
  color: #333;
  animation: ${fadeIn} 1s ease-in-out;
`;

const List = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;
`;

const ListItem = styled.li`
  font-size: 18px;
  margin-bottom: 10px;
  color: #555;
  animation: ${fadeIn} 1s ease-in-out;
`;

const StyledForm = styled.form`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-top: 20px;
  animation: ${fadeIn} 1s ease-in-out;

  & > * {
    margin-bottom: 10px;
  }
`;

class Debits extends Component {
  constructor(props) {
    super(props);
    this.state = {
      debits: [],
      newDebitDescription: '',
      newDebitAmount: ''
    };
  }

  componentDidMount() {
    // Fetch debit data from the API endpoint
    fetch('https://johnnylaicode.github.io/api/debits.json')
      .then(response => response.json())
      .then(data => this.setState({ debits: data }));
  }

  // Calculate total debits amount
  calculateTotalDebits = () => {
    return this.state.debits.reduce((total, debit) => total + debit.amount, 0);
  }

  // Handle input change for new debit description
  handleDescriptionChange = (event) => {
    this.setState({ newDebitDescription: event.target.value });
  }

  // Handle input change for new debit amount
  handleAmountChange = (event) => {
    this.setState({ newDebitAmount: event.target.value });
  }

  // Add new debit
  addDebit = () => {
    const { newDebitDescription, newDebitAmount } = this.state;
    if (newDebitDescription && newDebitAmount) {
      const currentDate = new Date().toISOString().slice(0, 10);
      const newDebit = {
        description: newDebitDescription,
        amount: parseFloat(newDebitAmount),
        date: currentDate
      };
      this.setState(prevState => ({
        debits: [...prevState.debits, newDebit],
        newDebitDescription: '',
        newDebitAmount: ''
      }), () => {
        // Update account balance after adding new debit
        this.props.updateAccountBalance();
      });
    }
  }

  render() {
    return (
      <Container>
        <Title>Debits</Title>

        <div>
          <h2>Account Balance: ${this.props.accountBalance}</h2>
        </div>

        <div>
          <input type="text" placeholder="Description" value={this.state.newDebitDescription} onChange={this.handleDescriptionChange} />
          <input type="number" placeholder="Amount" value={this.state.newDebitAmount} onChange={this.handleAmountChange} />
          <button onClick={this.addDebit}>Add Debit</button>
        </div>

        <StyledLink to="/">Return to Home</StyledLink>
      </Container>
    );
  }
}
export default Debits;
