import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import styled, { keyframes } from 'styled-components';
import AccountBalance from './AccountBalance'; // Import AccountBalance component

// Keyframes for animation
const fadeIn = keyframes`
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
`;

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  background-image: url('https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/325143b6-4839-4676-b7f8-6892f9ba66c4/dfn3efm-748d1037-4fef-4ba0-945d-a5132355bf1c.jpg/v1/fill/w_1280,h_854,q_75,strp/unusual_planets_season_2___8k_wallpapers_series__6_by_dolbozhuy_dfn3efm-fullview.jpg?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..CW50f3WxYmkK-uiM4HudxEpicIJ1r9I21NeR8HLtra8'); 
  background-size: cover;
  background-position: center;
  min-height: 100vh;
`;

const Title = styled.h1`
  font-size: 36px;
  margin-bottom: 20px;
  color: #333;
  animation: ${fadeIn} 1s ease-in-out;
`;

const StyledLink = styled(Link)`
  font-size: 18px;
  color: #007bff;
  text-decoration: none;
  margin-bottom: 10px;
  animation: ${fadeIn} 1s ease-in-out;

  &:hover {
    text-decoration: underline;
  }
`;

class Credits extends Component {
  constructor(props) {
    super(props);
    this.state = {
      credits: [],
      newCreditDescription: '',
      newCreditAmount: ''
    };
  }

  componentDidMount() {
    // Fetch credit data from the API endpoint
    fetch('https://johnnylaicode.github.io/api/credits.json')
      .then(response => response.json())
      .then(data => this.setState({ credits: data }));
  }

  // Calculate total credits amount
  calculateTotalCredits = () => {
    return this.state.credits.reduce((total, credit) => total + credit.amount, 0);
  }

  // Handle input change for new credit description
  handleDescriptionChange = (event) => {
    this.setState({ newCreditDescription: event.target.value });
  }

  // Handle input change for new credit amount
  handleAmountChange = (event) => {
    this.setState({ newCreditAmount: event.target.value });
  }

  // Add new credit
  addCredit = () => {
    const { newCreditDescription, newCreditAmount } = this.state;
    if (newCreditDescription && newCreditAmount) {
      const currentDate = new Date().toISOString().slice(0, 10);
      const newCredit = {
        description: newCreditDescription,
        amount: parseFloat(newCreditAmount),
        date: currentDate
      };
      this.setState(prevState => ({
        credits: [...prevState.credits, newCredit],
        newCreditDescription: '',
        newCreditAmount: ''
      }), () => {
        // Update account balance after adding new credit
        this.props.updateAccountBalance();
      });
    }
  }

  render() {
    return (
      <Container>
        <Title>Credits</Title>

        {/* Render AccountBalance component */}
        <AccountBalance />

        <div>
          <input type="text" placeholder="Description" value={this.state.newCreditDescription} onChange={this.handleDescriptionChange} />
          <input type="number" placeholder="Amount" value={this.state.newCreditAmount} onChange={this.handleAmountChange} />
          <button onClick={this.addCredit}>Add Credit</button>
        </div>

        <div>
          <h3>Credits List:</h3>
          <ul>
            {this.state.credits.map((credit, index) => (
              <li key={index}>{credit.description}: ${credit.amount.toFixed(2)} ({credit.date})</li>
            ))}
          </ul>
        </div>

        <StyledLink to="/">Return to Home</StyledLink>
      </Container>
    );
  }
}

export default Credits;
