import React, { Component } from 'react';

class AccountBalance extends Component {
  constructor(props) {
    super(props);
    this.state = {
      debits: [],
      accountBalance: 0
    };
  }

  // Recalculate account balance when props are updated
  componentDidUpdate(prevProps) {
    if (prevProps.credits !== this.props.credits) {
      this.calculateAccountBalance();
    }
  }

  // Calculate account balance
  calculateAccountBalance = () => {
    const totalCredits = this.props.credits.reduce((total, credit) => total + credit.amount, 0);
    const totalDebits = this.state.debits.reduce((total, debit) => total + debit.amount, 0);
    const accountBalance = totalCredits - totalDebits;
    this.setState({ accountBalance: accountBalance.toFixed(2) }); // Update account balance state
  }

  render() {
    return (
      <div>
        <h2>Account Balance</h2>
        <p>Balance: ${this.state.accountBalance}</p>
      </div>
    );
  }
}

export default AccountBalance;
